import React, { useState, useEffect } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import axios from 'axios';
import {allPlaceEndPoint} from "../../data/apiConstants";
import HandleException from "../../util/Toastify";
import Header from "../../components/Header";
import {Box} from "@mui/material";
import {useParams} from "react-router-dom";

function AddUpdateRoute() {
    let {id} = useParams();

    const [places, setPlaces] = useState([]);
    const [initialValues, setInitialValues] = useState({
        name: '',
        startDestination: '',
        endDestination: '',
    });

    useEffect(() => {
        // Fetch the list of places using Axios and populate the places state
        axios.get(allPlaceEndPoint)
            .then(response => {
                setPlaces(response.data.result);
            })
            .catch(error => {
                HandleException((error))
            });
    }, []);

    return (
        <Box m="20px">
            {id == null ? <Header title="Create Route" subtitle="Create a New Route"/> :
                <Header title="Update Route" subtitle="Update Route Details"/>}
        <div>
            <h1>Add or Update Route</h1>
            <Formik
                initialValues={initialValues}
                validate={values => {
                    const errors = {};
                    if (!values.name) {
                        errors.name = 'Name is required';
                    }
                    if (values.startDestination === values.endDestination) {
                        errors.startDestination = 'Start and end destinations cannot be the same';
                    }
                    return errors;
                }}
                onSubmit={(values, { resetForm }) => {
                    // Handle form submission, e.g., make a POST request to your API
                    console.log('Submitted values:', values);
                    // Reset the form after submission
                    resetForm();
                }}
            >
                <Form>
                    <div>
                        <label htmlFor="name">Name:</label>
                        <Field type="text" id="name" name="name" />
                        <ErrorMessage name="name" component="div" />
                    </div>

                    <div>
                        <label htmlFor="startDestination">Start Destination:</label>
                        <Field as="select" id="startDestination" name="startDestination">
                            <option value="" disabled>Select a start destination</option>
                            {places.map(place => (
                                <option key={place.id} value={place.id}>{place.name}</option>
                            ))}
                        </Field>
                        <ErrorMessage name="startDestination" component="div" />
                    </div>

                    <div>
                        <label htmlFor="endDestination">End Destination:</label>
                        <Field as="select" id="endDestination" name="endDestination">
                            <option value="" disabled>Select an end destination</option>
                            {places.map(place => (
                                <option key={place.id} value={place.id}>{place.name}</option>
                            ))}
                        </Field>
                        <ErrorMessage name="endDestination" component="div" />
                    </div>

                    <button type="submit">Submit</button>
                </Form>
            </Formik>
        </div>
        </Box>
    );
}

export default AddUpdateRoute;
