import React, {useEffect, useState} from 'react';
import axios from 'axios';
import * as yup from "yup";
import {allPlaceEndPoint, placeEndPoint, routeEndPoint} from "../../data/apiConstants";
import HandleException from "../../util/Toastify";
import Header from "../../components/Header";
import {Box, Button, TextField} from "@mui/material";
import {useParams} from "react-router-dom";
import useMediaQuery from "@mui/material/useMediaQuery";
import {Formik} from "formik";
import {toast} from "react-toastify";

function AddUpdateRoute() {
    let {id} = useParams();
    const isNonMobile = useMediaQuery("(min-width:600px)");


    const [route, setRoute] = useState({
        id: null,
        name:"",
        startDestination: {
            id:null,
            name:""
        },
        endDestination: {
            id: null,
            name:""
        }
    });
    const [places, setPlaces] = useState([]);

    const initialValues = {
        name: route.name,
        startDestination: route.startDestination.name,
        endDestination: route.endDestination.name
    }

    useEffect(() => {
        axios.get(allPlaceEndPoint)
            .then(response => {
                setPlaces(response.data.result);
            })
            .catch(error => {
                HandleException((error))
            });
    }, []);

    const handleFormSubmit = async (values) => {
        if (route.id == null) {
            axios.post(routeEndPoint, values)
                .then(() => {
                    toast.success("Successfully created route");
                    navigator(-1);
                })
                .catch((error) => {
                    HandleException(error);
                });
        } else {
            values.id = route.id
            axios.put(`${routeEndPoint}/${route.id}`, values)
                .then(() => {
                    toast.success("Successfully updated place");
                    navigator(-1);
                })
                .catch((error) => {
                    HandleException(error);
                });
        }
    };

    const checkoutSchema = yup.object().shape({
        name: yup.string().required('Name is required'),
        startDestination: yup.mixed().test(
            'is-different',
            'Start and end destinations cannot be the same',
            function (value) {
                const { endDestination } = this.parent;
                return value && endDestination && value.id !== endDestination.id;
            }
        ).required('Start Destination is required'),
        endDestination: yup.mixed().required('End Destination is required'),
    });

    return (
        <Box m="20px">
            {id == null ? <Header title="Create Route" subtitle="Create a New Route"/> :
                <Header title="Update Route" subtitle="Update Route Details"/>}
            <Formik
                enableReinitialize={true}
                onSubmit={handleFormSubmit}
                initialValues={initialValues}
                validationSchema={checkoutSchema}
            >
                {({
                      values, errors, touched, handleBlur, handleChange, handleSubmit,
                  }) => (<form onSubmit={handleSubmit}>
                    <Box
                        display="grid"
                        gap="30px"
                        gridTemplateColumns="repeat(4, minmax(0, 1fr))"
                        sx={{
                            "& > div": {gridColumn: isNonMobile ? undefined : "span 4"},
                        }}
                    >
                        <TextField
                            fullWidth
                            variant="filled"
                            type="text"
                            label="Name"
                            onBlur={handleBlur}
                            onChange={handleChange}
                            value={values.name} // Make sure this is correct
                            name="name"
                            error={!!touched.name && !!errors.name}
                            helperText={touched.name && errors.name}
                            sx={{gridColumn: "span 4"}}
                        />
                        <TextField
                            fullWidth
                            variant="filled"
                            type="number"
                            label="latitude"
                            onBlur={handleBlur}
                            onChange={handleChange}
                            value={values.latitude}
                            name="latitude"
                            error={!!touched.latitude && !!errors.latitude}
                            helperText={touched.latitude && errors.latitude}
                            sx={{gridColumn: "span 2"}}
                        />
                        <TextField
                            fullWidth
                            variant="filled"
                            type="number"
                            label="Longitude"
                            onBlur={handleBlur}
                            onChange={handleChange}
                            value={values.longitude}
                            name="longitude"
                            error={!!touched.longitude && !!errors.longitude}
                            helperText={touched.longitude && errors.longitude}
                            sx={{gridColumn: "span 2"}}
                        />
                    </Box>
                    <Box display="flex" justifyContent="end" mt="20px">

                        <Button type="submit" color="secondary" variant="contained">
                            {id == null ? "Create New Place" : "Update Place"}
                        </Button>
                    </Box>
                </form>)}
            </Formik>

        </Box>
    );
}

export default AddUpdateRoute;
