import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import { DataGrid, GridToolbar } from '@mui/x-data-grid';
import Button from '@mui/material/Button'; // Import Button from Material-UI
import axios from 'axios';
import Header from '../../components/Header';
import { allDriversEndPoint, allUserEndPoint, userEndPoint } from '../../data/apiConstants';
import HandleException from '../../util/Toastify';
import { PersonAddOutlined } from '@mui/icons-material';
import { useTheme } from '@emotion/react';
import { tokens } from '../../theme';

function Drivers() {
  const theme = useTheme();
  const colors = tokens(theme.palette.mode);
  const [data, setData] = useState([]);

  const columns = [
    { field: 'id', headerName: 'ID', flex: 1, hide: true },
    { field: 'firstName', headerName: 'First Name', flex: 1 },
    { field: 'lastName', headerName: 'Last Name', flex: 1 },
    { field: 'email', headerName: 'Email', flex: 1 },
    { field: 'phoneNumber', headerName: 'Phone Number', flex: 1 },
    { field: 'drivingLicenseNumber', headerName: 'Driving License Number', flex: 1 },
    {
      field: 'update',
      headerName: 'Update',
      flex: 0.5,
      renderCell: (params) => (
        <Button variant="outlined" color="success">
          Update
        </Button>
      ),
    },
    {
      field: 'delete',
      headerName: 'Delete',
      flex: 0.5,
      renderCell: (params) => (
        <Button variant="outlined" color="error" onClick={() => handleDelete(params.row.id)}>
          Delete
        </Button>
      ),
    },
  ];

  const fetchData = () => {
    axios.get(allDriversEndPoint)
    .then((response) => {
      setData(response.data.result);
    })
    .catch((error) => {
      HandleException(error)
    });
  };

  useEffect(() => {
    fetchData(); // Fetch data when the component mounts
  }, []);

  const handleDelete = (id) => {
    axios.delete(userEndPoint+id)
      .then((response) => {
        fetchData();
      })
      .catch((error) => {
        HandleException(error)
      });
  };

  return (
    <Box m="20px">
      <Header
        title="Drivers"
        subtitle="Manage Drivers"
      />
        <Box>
          <Button
            sx={{
              backgroundColor: colors.blueAccent[700],
              color: colors.grey[100],
              fontSize: "14px",
              fontWeight: "bold",
              padding: "10px 20px",
            }}
          >
            <PersonAddOutlined sx={{ mr: "10px" }} />
            Add Driver
          </Button>
        </Box>
      <Box
        m="40px 0 0 0"
        height="75vh"
        sx={{
          // Your styling here
        }}
      >
        <DataGrid
          rows={data}
          columns={columns}
          components={{ Toolbar: GridToolbar }}
        />
      </Box>
    </Box>
  );
}

export default Drivers;
