import React, { useState, useEffect } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import axios from 'axios';
import {
    TextField,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Button,
    Box,
} from '@mui/material';
import {allPlaceEndPoint} from "../../data/apiConstants";

function AddUpdateRoute() {
    const [places, setPlaces] = useState([]);
    const [initialValues, setInitialValues] = useState({
        name: '',
        startDestination: '',
        endDestination: '',
    });

    useEffect(() => {
        // Fetch the list of places using Axios and populate the places state
        axios
            .get(allPlaceEndPoint)
            .then((response) => {
                setPlaces(response.data.result);
            })
            .catch((error) => {
                console.error('Error fetching places:', error);
            });
    }, []);

    return (
        <Box p={2}>
            <h1>Add or Update Route</h1>
            <Formik
                initialValues={initialValues}
                validate={(values) => {
                    const errors = {};
                    if (!values.name) {
                        errors.name = 'Name is required';
                    }
                    if (values.startDestination === values.endDestination) {
                        errors.startDestination = 'Start and end destinations cannot be the same';
                    }
                    return errors;
                }}
                onSubmit={(values, { resetForm }) => {
                    // Handle form submission, e.g., make a POST request to your API
                    console.log('Submitted values:', values);
                    // Reset the form after submission
                    resetForm();
                }}
            >
                <Form>
                    <Box mt={2}>
                        <TextField
                            id="name"
                            name="name"
                            label="Name"
                            variant="outlined"
                            fullWidth
                        />
                        <ErrorMessage name="name" component="div" />
                    </Box>

                    <Box mt={2}>
                        <FormControl variant="outlined" fullWidth>
                            <InputLabel id="startDestination-label">Start Destination</InputLabel>
                            <Field
                                as={Select}
                                labelId="startDestination-label"
                                label="Start Destination"
                                id="startDestination"
                                name="startDestination"
                            >
                                <MenuItem value="">
                                    <em>Select a start destination</em>
                                </MenuItem>
                                {places.map((place) => (
                                    <MenuItem key={place.id} value={place.id}>
                                        {place.name}
                                    </MenuItem>
                                ))}
                            </Field>
                        </FormControl>
                        <ErrorMessage name="startDestination" component="div" />
                    </Box>

                    <Box mt={2}>
                        <FormControl variant="outlined" fullWidth>
                            <InputLabel id="endDestination-label">End Destination</InputLabel>
                            <Field
                                as={Select}
                                labelId="endDestination-label"
                                label="End Destination"
                                id="endDestination"
                                name="endDestination"
                            >
                                <MenuItem value="">
                                    <em>Select an end destination</em>
                                </MenuItem>
                                {places.map((place) => (
                                    <MenuItem key={place.id} value={place.id}>
                                        {place.name}
                                    </MenuItem>
                                ))}
                            </Field>
                        </FormControl>
                        <ErrorMessage name="endDestination" component="div" />
                    </Box>

                    <Box display="flex" justifyContent="end" mt="20px">

                        <Button type="submit" color="secondary" variant="contained">
