import React, {useEffect, useState} from 'react';
import Box from '@mui/material/Box';
import {DataGrid, GridToolbar} from '@mui/x-data-grid';
import Button from '@mui/material/Button'; // Import Button from Material-UI
import axios from 'axios';
import Header from '../../components/Header';
import {allUserEndPoint, userEndPoint} from '../../data/apiConstants';
import HandleException from '../../util/Toastify';
import {PersonAddOutlined} from '@mui/icons-material';
import {useTheme} from '@emotion/react';
import {tokens} from '../../theme';
import {NavLink} from 'react-router-dom';
import {toast} from "react-toastify";

const Users = () => {

    const theme = useTheme();
    const colors = tokens(theme.palette.mode);
    const [data, setData] = useState([]);
    const handleDeleteClick = async(id) => {
        await axios.delete(`${userEndPoint}/${id}`)
            .then(() => {
                toast.success("Successfully deleted user");
                setData((prevData) => prevData.filter(item => item.id !== id));
            })
            .catch((error) => {
                HandleException(error);
            });
    }

    const columns = [
    { field: 'id', headerName: 'ID', flex: 1, hide: true }, // Assuming you have an 'id' field in your data
    { field: 'firstName', headerName: 'First Name', flex: 1 },
    { field: 'lastName', headerName: 'Last Name', flex: 1 },
    { field: 'email', headerName: 'Email', flex: 1 },
    { field: 'phoneNumber', headerName: 'Phone Number', flex: 1 },
    {
      field: 'update',
      headerName: 'Update',
      flex: 0.5,
      renderCell: (params) => (
      <NavLink
          to={{
              pathname: `/addUpdateUser/${params.row.id}`,
          }}
      >
          <Button variant="outlined" color="success">
              Update
          </Button>
      </NavLink>
      ),
    },
    {
      field: 'delete',
      headerName: 'Delete',
      flex: 0.5,
      renderCell: (params) => (
        <Button variant="outlined" color="error"  onClick={() => handleDeleteClick(params.row.id)}>
          Delete
        </Button>
      ),
    },
  ];

  useEffect(() => {
    axios.get(allUserEndPoint)
      .then((response) => {
        setData(response.data.result);
      })
      .catch((error) => {
        HandleException(error)
      });
  }, []);

  return (
    <Box m="20px">
      <Header
        title="Users"
        subtitle="Manage Users"
      />
        <Box>
            <NavLink
                to={{
                    pathname: '/addUpdateUser',
                }}
            >
          <Button
            sx={{
              backgroundColor: colors.blueAccent[700],
              color: colors.grey[100],
              fontSize: "14px",
              fontWeight: "bold",
              padding: "10px 20px",
            }}
          >
            <PersonAddOutlined sx={{ mr: "10px" }} />
            Add User
          </Button>
            </NavLink>
        </Box>
      <Box
        m="40px 0 0 0"
        height="75vh"
        sx={{
          // Your styling here
        }}
      >
        <DataGrid
          rows={data}
          columns={columns}
          components={{ Toolbar: GridToolbar }}
        />
      </Box>
    </Box>
  );
}

export default Users;
